func main() {
	token := os.Getenv("HCLOUD_TOKEN")

	client := hcloud.NewClient(hcloud.WithToken(token))
	ctx := context.TODO()

	result, _, err := client.StorageBox.CreateSubaccount(ctx, &hcloud.StorageBox{ID: 123}, hcloud.StorageBoxSubaccountCreateOpts{
		HomeDirectory: "my-backup/server01",
		Password:      "string",
		AccessSettings: &hcloud.StorageBoxSubaccountCreateOptsAccessSettings{
			ReachableExternally: hcloud.Ptr(false),
			Readonly:            hcloud.Ptr(false),
			SambaEnabled:        hcloud.Ptr(false),
			SSHEnabled:          hcloud.Ptr(false),
			WebDAVEnabled:       hcloud.Ptr(false),
		},
		Description: "my-backup-server01",
		Labels: map[string]string{
			"environment":    "prod",
			"example.com/my": "label",
			"just-a-key":     "",
		},
	})

	err = client.Action.WaitFor(ctx, result.Action)

	subaccount := result.Subaccount
}

